mapper.sql:
1 = 1

    AND user_name LIKE concat(#{userName}, '%')


    AND UNIX_TIMESTAMP(create_time)*1000 >= #{startCreateTime}


    AND UNIX_TIMESTAMP(create_time)*1000 <= #{endCreateTime}


    AND UNIX_TIMESTAMP(last_login_time)*1000 >= #{startLastLoginTime}


    AND UNIX_TIMESTAMP(last_login_time)*1000 <= #{endLastLoginTime}

mapper.update:
UPDATE SYS_USER
SET
    last_login_host = #{lastLoginHost},
    last_login_time = #{lastLoginTime}
WHERE id = #{id}

mapper.select:
SELECT COUNT(1)
FROM sys_user
WHERE 1 = 1

    AND id <> #{id}

AND user_name = #{userName}
